mapper.update:

UPDATE BOARD
SET BOA_UP = NVL(BOA_UP,0) + 1
WHERE BOA_NUM = #{boa_num}

mapper.select:

SELECT BOA_UP
FROM BOARD
WHERE BOA_NUM = #{boa_num}

mapper.update:

UPDATE BOARD
SET BOA_DOWN = NVL(BOA_DOWN,0) + 1
WHERE BOA_NUM = #{boa_num}

mapper.select:

SELECT BOA_DOWN
FROM BOARD
WHERE BOA_NUM = #{boa_num}
