SELECT cat_c, cat_name, cat_p
FROM CATEGORY
WHERE cat_p = #{categoryCode}


SELECT RN, ad_boa_num, ad_boa_c, ad_boa_p, ad_boa_title, ad_boa_content, ad_boa_date_reg, ad_boa_date_up
FROM (
    SELECT  /*+ INDEX_DESC(AD_BOARD PK_BOA_NUM)*/
    ROWNUM RN, ad_boa_num, ad_boa_c, ad_boa_p, ad_boa_title, ad_boa_content, ad_boa_date_reg, ad_boa_date_up
    FROM AD_BOARD
    WHERE  





         ROWNUM <= #{pageNum} * #{amount}
)
WHERE RN > (#{pageNum}-1) * #{amount}


SELECT ad_boa_num, ad_boa_c, ad_boa_p, ad_boa_title, ad_boa_content, ad_boa_date_reg, ad_boa_date_up
FROM AD_BOARD
WHERE ad_boa_num = #{ad_boa_num}
