mapper.update:

UPDATE TBL_ORDER
SET ODR_STATUS = #{odr_status}
WHERE ODR_CODE = #{odr_code}

mapper.delete:

DELETE TBL_ORDER_DETAIL
WHERE ODR_CODE = #{odr_code}
AND PDT_NUM = ${pdt_num}

mapper.update:

UPDATE TBL_ORDER
SET ODR_TOTAL_PRICE = ODR_TOTAL_PRICE - #{unit_price}
WHERE ODR_CODE = #{odr_code}


UPDATE TBL_PAYMENT
SET PAY_TOT_PRICE = PAY_TOT_PRICE - #{unit_price}
WHERE ODR_CODE = #{odr_code}
