mapper.select:

SELECT MEM_ID
FROM TBL_MEMBER
WHERE MEM_ID = #{MEM_ID}


SELECT MEM_ID
FROM TBL_MEMBER
WHERE MEM_NAME = #{mem_name}
AND MEM_EMAIL = #{mem_email}


SELECT MEM_ID, MEM_EMAIL
FROM TBL_MEMBER
WHERE MEM_ID = #{mem_id} AND MEM_EMAIL = #{mem_email}

mapper.update:

UPDATE TBL_MEMBER
SET MEM_PW = #{mem_pw}
WHERE MEM_ID = #{mem_id}

UPDATE TBL_MEMBER SET MEM_NAME = #{mem_name},
MEM_PW = #{mem_pw},
MEM_ZIPCODE = #{mem_zipcode}, MEM_ADDR = #{mem_addr}, MEM_ADDR_D = #{mem_addr_d}, MEM_PHONE = #{mem_phone}, MEM_NICK = #{mem_nick}, MEM_EMAIL = #{mem_email}, MEM_ACCEPT_E = #{mem_accept_e} WHERE MEM_ID = #{mem_id}
